/*
Notes from Ash: 
I've modified the newFruit.js and renamed it to fruitForm.js

This form has three buttons now: Create Fruit, Update Fruit and Delete Fruit

This form also has a new input to input the fruit id at the top
The form only reads and requires this id while UPDATING and DELETING a fruit
When CREATING a fruit, the fruitId text field is ignored!


Each button has an "id" and each button has a function which listens for button clicks

When the create button is clicked, we only take the name, color, age, readyToEat and categoryId
AND 
make a POST request to create the fruit

When the update button is clicked, we take all the inputs for fruit and use the fruit id to update a particular fruit


When the delete button is clicked, we take the fruit id and make a DELETE request to delete it
*/

const form = `
<form>
<h1>Fruit form</h1>
  <div class="form-group">
  <div class="form-group">
    <label for="fruitId">Fruit Id (Only used for update/delete)</label>
    <input type="text" class="form-control" id="fruitId" placeholder="Enter fruit Id" name="fruitId">
  </div>
    <label for="name">Name</label>
    <input type="text" class="form-control" id="name" placeholder="Enter a fruit name" name="name">
  </div>
  <div class="form-group">
    <label for="color">Color</label>
    <input type="text" class="form-control" id="color" placeholder="Enter color of the fruit" name="color">
  </div>
  <div class="form-group">
    <label for="age">Age</label>
    <input type="text" class="form-control" id="age" placeholder="Enter fruit's age" name="age">
  </div>
  <fieldset class="form-group">
    <legend class="col-form-label">Ready to Eat?</legend>
    <div class="form-check form-check-inline">
      <input class="form-check-input" type="radio" id="readyToEatYes" name ="readyToEat" value="true">
      <label class="form-check-label" for="readyToEatYes">Yes</label>
    </div>
    <div class="form-check form-check-inline">
      <input class="form-check-input" type="radio" id="readyToEatNo" name ="readyToEat" value="false">
      <label class="form-check-label" for="readyToEatNo">No</label>
    </div>
  </fieldset>
  <div class="form-group">
    <label for="categoryId">Category</label>
    <select name="categoryId" id="categories"></select>
  </div>
  <button type="button" id="create-fruit"  class="btn btn-primary">Create Fruit</button>
  <button  type="button" id="update-fruit"  class="btn btn-primary">Update Fruit</button>
  <button  type="button" id="delete-fruit"  class="btn btn-primary">Delete Fruit</button>
  </form>
`;

const fruitForm = () => {
  // This logic below gets all categories and loads it in the dropdown

  // Call server using AJAX to get all categories
  const categoryResponse = $.ajax({
    type: "GET",
    url: "/api/fruits/category/all",
  }).done((fruitCategories) => {
    let optionsHtml = "";
    /*
    Iterate over all categories
    Construct a dynamic <option> tag for each category
    Append your optionsHtml to the <select> tag with id "categories"
    */
    fruitCategories.forEach((fruitEl) => {
      optionsHtml += `<option value=${fruitEl._id}>${fruitEl.name}</option>`;
    });

    $("#categories").append(optionsHtml);
  });

  // Event listener to for Create Fruit Button
  $(document).on("click", "#create-fruit", async (e) => {
    e.preventDefault();

    // Construct body by extracting info from the form
    const requestBody = {
      name: $("#name").val(),
      color: $("#color").val(),
      age: $("#age").val(),
      readyToEat: $(`input[name="readyToEat"]:checked`).val(),
      categoryId: $("#categories").val(),
    };

    // Make a POST request to the server to create a fruit
    const response = await $.ajax({
      type: "POST",
      url: "/api/fruits/new-fruit",
      contentType: "application/json",
      data: JSON.stringify(requestBody),
    });

    // Create a pop up alert in the UI to inform the user that fruit was created
    window.alert("Fruit Created!");
  });

  // Event listener to for Update Fruit Button
  $(document).on("click", "#update-fruit", async (e) => {
    e.preventDefault();

    // Construct body by extracting info from the form
    const requestBody = {
      name: $("#name").val(),
      color: $("#color").val(),
      age: $("#age").val(),
      readyToEat: $(`input[name="readyToEat"]:checked`).val(),
      categoryId: $("#categories").val(),
    };

    // Make a PATCH request to the server to update a fruit
    const response = await $.ajax({
      type: "PATCH",
      url: `/api/fruits/update-fruit/${$("#fruitId").val()}`,
      contentType: "application/json",
      data: JSON.stringify(requestBody),
    });

    // Create a pop up alert in the UI to inform the user that fruit was updated
    window.alert("Fruit Updated!");
  });

  // Event listener to for Delete Fruit Button
  $(document).on("click", "#delete-fruit", async (e) => {
    e.preventDefault();

    // Make a DELETE request to the server to delete a fruit
    const response = await $.ajax({
      type: "DELETE",
      url: `/api/fruits/delete-fruit/${$("#fruitId").val()}`,
      contentType: "application/json",
    });

    // Create a pop up alert in the UI to inform the user that fruit was deleted
    window.alert("Fruit Deleted!");
  });

  return form;
};

export default fruitForm;
